mapper.insert:
        
            SELECT seq_board_id.CURRVAL FROM DUAL
        
        
        INSERT INTO board_tbl (
            board_id, board_title, board_content, board_use, member_id, board_num, board_file_path
        ) VALUES (
            seq_board_id.NEXTVAL,
            #{boardTitle}, #{boardContent}, #{boardUse, jdbcType=CHAR}, #{memberId},
            #{boardNum, jdbcType=CHAR}, #{boardFilePath, jdbcType=VARCHAR}
        )
